$(document).ready(function() {
    function loadPage(pageNumber) {
        $.ajax({
            url: '/getInventory',
            method: 'GET',
            data: { page: pageNumber },
            success: function(response) {
                let inventoryHtml = '';
                response.managementInventory.forEach(item => {
                    // Xác định lớp CSS cho QuantityTotal dựa trên giá trị
                    let quantityClass = '';
                    if (item.QuantityTotal < 10) {
                        quantityClass = 'blinking-red';
                    } else if (item.QuantityTotal < 100) {
                        quantityClass = 'blinking-yellow';
                    }

                    inventoryHtml += `<tr>
                                        <td class="text-center align-middle">${item.ID}</td>
                                        <td class="text-center align-middle">${item.Epicor}</td>
                                        <td class="text-center align-middle">
                                          ${item.EngName}<br>
                                          <span class="text-xs text-secondary">${item.VNName}</span>
                                        </td>
                                        <td class="text-center align-middle">${item.Manufacturer}</td>
                                        <td class="text-center align-middle">${item.POCode}</td>
                                        <td class="text-center align-middle ${quantityClass}">${item.QuantityTotal}</td>
                                        <td class="text-center align-middle">${item.Inputer}</td>
                                        <td class="text-center align-middle">
                                          ${new Date(item.DateInput).toLocaleString('vi-VN', {
                                            day: '2-digit', month: '2-digit', year: 'numeric',
                                            hour: '2-digit', minute: '2-digit'
                                          })}
                                        </td>
                                        <td class="text-center align-middle">
                                          ${new Date(item.DateUpdate).toLocaleString('vi-VN', {
                                            day: '2-digit', month: '2-digit', year: 'numeric',
                                            hour: '2-digit', minute: '2-digit'
                                          })}
                                        </td>
                                      </tr>`;
                });
                $('#inventory-list').html(inventoryHtml);

                // Cập nhật số lượng sản phẩm
                $('#productCount').text(response.productCount);

                let paginationHtml = '';
                let totalPages = response.totalPages;
                let startPage = Math.max(1, pageNumber - 2);
                let endPage = Math.min(totalPages, pageNumber + 2);

                if (totalPages > 1) {
                    if (pageNumber > 1) {
                        paginationHtml += `<a href="#" class="page-link" data-page="${pageNumber - 1}">Previous</a>`;
                    } else {
                        paginationHtml += `<span class="page-link disabled">Previous</span>`;
                    }

                    if (startPage > 1) {
                        paginationHtml += `<a href="#" class="page-link" data-page="1">1</a>`;
                        if (startPage > 2) {
                            paginationHtml += `<span class="page-link disabled">...</span>`;
                        }
                    }

                    for (let i = startPage; i <= endPage; i++) {
                        let activeClass = (i === pageNumber) ? 'active' : '';
                        paginationHtml += `<a href="#" class="page-link ${activeClass}" data-page="${i}">${i}</a>`;
                    }

                    if (endPage < totalPages) {
                        if (endPage < totalPages - 1) {
                            paginationHtml += `<span class="page-link disabled">...</span>`;
                        }
                        paginationHtml += `<a href="#" class="page-link" data-page="${totalPages}">${totalPages}</a>`;
                    }

                    if (pageNumber < totalPages) {
                        paginationHtml += `<a href="#" class="page-link" data-page="${pageNumber + 1}">Next</a>`;
                    } else {
                        paginationHtml += `<span class="page-link disabled">Next</span>`;
                    }
                }
                $('#pagination').html(paginationHtml);
            },
            error: function(xhr, status, error) {
                console.error('Lỗi khi tải dữ liệu:', error);
            }
        });
    }

    $('#pagination').on('click', '.page-link', function(e) {
        e.preventDefault();
        const pageNumber = $(this).data('page');
        loadPage(pageNumber);
    });

    loadPage(1);
});
